%psql.sql

drop table if exists web_clicks_linregr;
drop table if exists web_clicks_linregr_summary;

SELECT madlib.linregr_train('web_clicks','web_clicks_linregr','clicks_in_category',
'ARRAY[1,college_education,male,clicks_in_1,clicks_in_2,clicks_in_3,clicks_in_4,clicks_in_5,clicks_in_6,clicks_in_7]');

%psql.sql

SELECT web_clicks.*,
       madlib.linregr_predict( ARRAY[1,college_education,male,clicks_in_1,clicks_in_2,clicks_in_3,clicks_in_4,clicks_in_5,clicks_in_6,clicks_in_7],
                               m.coef
                             ) as predict,
        clicks_in_category -
          madlib.linregr_predict(ARRAY[1,college_education,male,clicks_in_1,clicks_in_2,clicks_in_3,clicks_in_4,clicks_in_5,clicks_in_6,clicks_in_7],
                                  m.coef
                                ) as residual
FROM web_clicks, web_clicks_linregr m;
